class Solution {    public List&lt;Integer&gt; findAnagrams(String s, String p) {        List&lt;Integer&gt; res=new ArrayList&lt;&gt;();        if(s.length()&lt;p.length()) return res;        Map&lt;Character, Integer&gt; targetMap=new HashMap&lt;&gt;(); // ç›®æ ‡çª—å£        Map&lt;Character, Integer&gt; windowMap=new HashMap&lt;&gt;(); // æ»‘åŠ¨çª—å£è®°å½•        for(char c:p.toCharArray()){            targetMap.put(c, targetMap.getOrDefault(c,0)+1);        }        int start=0, end=0; // æ»‘åŠ¨çª—å£æŒ‡é’ˆ        int count=0;        while(end&lt;s.length()){            char cur=s.charAt(end++);            if(targetMap.containsKey(cur)){                windowMap.put(cur, windowMap.getOrDefault(cur, 0)+1);                if(windowMap.get(cur).equals(targetMap.get(cur))) count++; // æ³¨æ„ï¼šè¿™é‡Œæ˜¯Integerï¼Œä¸€å®šå¾—ç”¨equalsï¼Œè€Œä¸æ˜¯==            }            while(targetMap.size()==count){                if(end-start==p.length()){                    res.add(start);                }                char expired=s.charAt(start++);                if(targetMap.containsKey(expired)){                    if(targetMap.get(expired).equals(windowMap.get(expired))) count--;                    windowMap.put(expired, windowMap.get(expired)-1);                }            }        }        return res;    }}

