class Solution {    public boolean checkInclusion(String s1, String s2) {        if(s1.length()&gt;s2.length()) return false;        Map&lt;Character, Integer&gt; targetMap=new HashMap&lt;&gt;(); // ç›®æ ‡çª—å£        Map&lt;Character, Integer&gt; windowMap=new HashMap&lt;&gt;(); // æ»‘åŠ¨çª—å£è®°å½•        for(char c:s1.toCharArray()){            targetMap.put(c, targetMap.getOrDefault(c,0)+1);        }        int start=0, end=0; // æ»‘åŠ¨çª—å£æŒ‡é’ˆ        int count=0;        while(end&lt;s2.length()){            char cur=s2.charAt(end++);            if(targetMap.containsKey(cur)){                windowMap.put(cur, windowMap.getOrDefault(cur, 0)+1);                if(windowMap.get(cur).equals(targetMap.get(cur))) count++; // æ³¨æ„ï¼šè¿™é‡Œæ˜¯Integerï¼Œä¸€å®šå¾—ç”¨equalsï¼Œè€Œä¸æ˜¯==                while(count==targetMap.size()){                    if(end-start==s1.length()){return true;}                    char expired=s2.charAt(start++);                    if(windowMap.get(expired).equals(targetMap.get(expired))) count--;                    windowMap.put(expired, windowMap.get(expired)-1);                }            }else{                windowMap=new HashMap&lt;&gt;();                count=0;                start=end;            }        }        return false;    }}

